var video = document.getElementById("vid");
var audioText = document.getElementById("audioButton");
var videoText = document.getElementById("videoButton");

video.volume = 0.1;


function videoPlay() {
if (video.paused) {
    video.play();
    videoText.innerHTML = "pause video";
  } else {
    video.pause();
    videoText.innerHTML = "resume video";
  }
}

function audioPlay() {
 if (video.volume == 0) {
  video.volume = 0.1;
  audioText.innerHTML = "mute video";
} else {
  video.volume = 0;
  audioText.innerHTML = "unmute video";
}
}

var mainVideo = $('#vid');
var mobileSrc = "https://files.catbox.moe/v0dgib.mp4";
var desktopSrc = "https://files.catbox.moe/pxh5r1.mp4";
 
$(document).ready(function () {
  // Initial source based on window width
  if ($(window).width() < 980) {
      mainVideo.attr("src", mobileSrc);
      console.log("mobile");
  } else {
      mainVideo.attr("src", desktopSrc);
      console.log("desktop");
  }

  // Update source on window resize
  $(window).resize(function () {
      if ($(window).width() < 980) {
          mainVideo.attr("src", mobileSrc);
          console.log("mobile");
      } else {
          mainVideo.attr("src", desktopSrc);
          console.log("desktop");
      }
  });
});
